// Class methods

common_model::common_model(void) : m_inv_right_calf(c_inv_right_calf, c_inv_right_calf + sizeof(c_inv_right_calf) / sizeof(int)),
                                   m_inv_right_thigh(c_inv_right_thigh, c_inv_right_thigh + sizeof(c_inv_right_thigh) / sizeof(int)),
                                   m_inv_right_uarm(c_inv_right_uarm, c_inv_right_uarm + sizeof(c_inv_right_uarm) / sizeof(int))
{ }

std::vector<int> &common_model::getInvRightCalf(void) const {
    return const_cast<std::vector<int>&>(m_inv_right_calf);
}
std::vector<int> &common_model::getInvRightThigh(void) const {
    return const_cast<std::vector<int>&>(m_inv_right_thigh);
}
std::vector<int> &common_model::getInvRightUpperArm(void) const {
    return const_cast<std::vector<int>&>(m_inv_right_uarm);
}

